function ProfileSidebar(props) {
  return (
      <Box as="aside">
        <img style={{ borderRadius: "8px" }} src={`https://github.com/${props.user}.png`}/>
        <hr/>
        <p>
          <a className="boxLink" href={`https://github.com/${props.user}`}>
            @{props.user}
          </a>
        </p>
        <hr/>

        <AlurakutProfileSidebarMenuDefault />

      </Box>      
    )
}

